import classes from './CartItem.module.css';
import { useDispatch ,useSelector} from 'react-redux';
import { productAction } from '../../redux/productincrease';

const CartItem = (props) => {
  const productnumber=useSelector((state)=>state.counter.number)
  const { title, total, price } = props.item;
  const dispatch=useDispatch();
  const increaseHanlder=()=>{
    dispatch(productAction.productincrease())
  }
  const decreaseHanlder=()=>{
    dispatch(productAction.productdecrease())
  }
  return (
    <li className={classes.item}>
      <header>
        <h3>{title}</h3>
        <div className={classes.price}>
          ${total.toFixed(2)}{' '}
          <span className={classes.itemprice}>(${price.toFixed(2)}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{productnumber}</span>
        </div>
        <div className={classes.actions}>
          <button onClick={decreaseHanlder}>-</button>
          <button onClick={increaseHanlder}>+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
