import { createSlice } from "@reduxjs/toolkit"

const initialproduct={items:[],quantity:0}
const productsincdec=createSlice({
    name:"cart",
    initialState:{initialproduct},
    reducers:{
        productincrease(state,action)
        {
           const newitem=action.payload;
           const existing=state.items.find(item=>item.id===newitem.id)
           if(!existing){
            state.items.push({id:newitem.id,price:newitem.price,title:newitem.title,totalprice:newitem.price,quantity:1})
           }
        },
        productdecrease(state)
        {
            state.number--;
        }
    }
})

export const productAction=productsincdec.actions;
export default productsincdec.reducer;
